using Mono.Cecil.Cil;using UnityEngine;public class ParallaxLayer : MonoBehaviour{    private float startPos, length;    public Transform cam;    public float parallaxEffect;    private void Start()    {        startPos = transform.position.x;        length = GetComponentInChildren<SpriteRenderer>().bounds.size.x;    }    private void LateUpdate()    {        float distance = cam.transform.position.x * parallaxEffect;        float movement = cam.transform.position.x * (1f - parallaxEffect);        transform.position = new Vector3(startPos + distance, transform.position.y, transform.position.z);                if (movement > startPos + (length/2))        {            startPos += length;        }        else if (movement < startPos - (length / 2))        {            startPos -= length;        }    }}